import React, {useState,useEffect} from 'react'
import { Wrapper,Back,Container ,Image, InfoContainer, Title, InfoColumns, Column,Info,BordersContainer, Button,Strong,} from './styles'

import { Link} from 'react-router-dom'
import {BsArrowLeft} from 'react-icons/bs'
import { useCountriesContext } from '../ContextCountries/context'
function DetailPage({match,theme}) {
    
    const {countries,setCountries} = useCountriesContext()
    const [country,setCountry] = useState([])
    const [loading,setLoading] = useState(true)
    const [nativeName,setNativeName] = useState([])
    const [currencies,setCurrencies] = useState([])
    const [languages,setLanguages] = useState()
    const [langs,setLangs] = useState([])
    const [borders,setBorders] = useState([])
    const [convertedBorders,setConvertedBorders] = useState([])
    const [filterBorder,setFilterBorder] = useState([])
    
    
    useEffect(() =>{
        setLangs([])
        setFilterBorder([])
        fetch('https://restcountries.com/v3.1/alpha/' + match.params.id )
        .then(res => res.json())
        .then(data => {
            setCountry(data[0])
            setNativeName(data[0].name.nativeName)
            setCurrencies(data[0].currencies)
            setLanguages(data[0].languages)
            setBorders(data[0].borders)
            setLoading(false)
        })

    }, [match.params.id])

    //Converts the language objects into an array
    useEffect(() =>{
        
        for(let lang in languages){
            setLangs( langs =>[...langs,languages[lang]])
        }
    
    },[languages])
    
    //Create a array of ojc with id as country cca3 and name as country name
    useEffect(() =>{

        
        for(let c in countries){
            setConvertedBorders(convertedBorders =>[...convertedBorders, {
                name : countries[c].name.common,
                id: countries[c].cca3
            }])
        }
        
    },[countries])

    //Compares the obj array with the borders arrays to match the border and cca3 parameters
    useEffect(()=>{
        if(borders){
        let filter = convertedBorders.filter(item => borders.includes(item.id))
        setFilterBorder(filterBorder =>[...filterBorder,...filter])
        }
    },[borders])

    if(loading){
        return <div>loading</div>
    }

    
    
    
    return (    
        <Wrapper className = 'container'>
            <Link to ='/'>
                <Back><BsArrowLeft color = {theme ==='dark' ? '#fff' : '#000'} size = {22}/>Back</Back>
            </Link>
            <Container >
                <Image src = {country.flags.svg}/>
                <InfoContainer>
                    <Title>{country.name.common}</Title>
                    <InfoColumns>
                        <Column>
                            <Info><Strong>Native Name:</Strong> { country.name.nativeName ? Object.values(nativeName)[0].common : 'No native name'}</Info>
                            <Info><Strong>Population:</Strong> {country.population}</Info>
                            <Info><Strong>Region:</Strong> {country.region}</Info>
                            <Info><Strong>Sub Region:</Strong> {country.subregion ? country.subregion : 'No subregions'}</Info>
                            <Info><Strong>Capital:</Strong> {country.capital ? country.capital : 'No capital'}</Info>
                        </Column>
                        <Column>
                            <Info><Strong>Top Level Domain:</Strong> {country.tld}</Info>
                            <Info><Strong>Currencies:</Strong> { country.currencies ?Object.values(currencies)[0].name : 'No currencies'}</Info>
                            <Info><Strong>Languages:</Strong> 
                                { langs.length > 0 ? langs.map((item,index) =>(
                                <div key = {index}>
                                    {index !== langs.length -1 ? item + ',' : item}
                                </div>)) : 'No languages'}
                            </Info>
                        </Column>
                    </InfoColumns>
                    <BordersContainer>
                        <Strong>Border Countries:</Strong>
                            {  filterBorder.length > 0 ? filterBorder.map((item,index) => (
                                <Link to = {'/details/' + item.id} key = {index} ><Button>{item.name}</Button></Link>
                                
                            )): <div>No borders</div>}
                        
                    </BordersContainer>
                </InfoContainer>
            </Container>
        </Wrapper>
    )
}

export default DetailPage
