    flex-direction:column;
    justify-content:flex-start;
    align-items:flex-start;
    height:100%;
    margin-right:50px;
`;
export const Info = styled.div`
    display:flex;
    flex-wrap:wrap;
    margin-bottom: 10px;
    
`;
export const Strong = styled.strong`
    margin-right:5px;
  
`;
