import React, {useState,useEffect} from 'react'
import { Wrapper,Back,Container ,Image, InfoContainer, Title, InfoColumns, Column,Info} from './styles'

function DetailPage({match}) {

    const [ country,setCountry] = useState([])
    const [loading,setLoading] = useState(true)
    useEffect(() =>{
        
        fetch('https://restcountries.com/v3.1/alpha/' + match.params.id )
        .then(res => res.json())
        .then(data => {
            setCountry(data[0])
            setLoading(false)
        })
        

    }, [])

    if(loading){
        return <div>loading</div>
    }
    return (    
        <Wrapper>
            <Back>Back</Back>
            <Container>
                <Image src = {country.flags.svg}/>
                <InfoContainer>
                    <Title>{country.name.common}</Title>
                    <InfoColumns>
                        <Column>
                        <Info></Info>
                        </Column>
                        <Column>
                        </Column>
                    </InfoColumns>
                </InfoContainer>
            </Container>
        </Wrapper>
    )
}

export default DetailPage
