import React, {useState,useEffect} from 'react'
import { Wrapper,Back,Container ,Image, InfoContainer, Title, InfoColumns, Column,Info, Strong, BordersContainer, BorderLabel, Button} from './styles'

function DetailPage({match}) {

    const [ country,setCountry] = useState([])
    const [loading,setLoading] = useState(true)
    const [nativeName,setNativeName] = useState([])
    const [currencies,setCurrencies] = useState([])
    const [languages,setLanguages] = useState()
    useEffect(() =>{
        
        fetch('https://restcountries.com/v3.1/alpha/' + match.params.id )
        .then(res => res.json())
        .then(data => {
            setCountry(data[0])
            setNativeName(data[0].name.nativeName)
            setCurrencies(data[0].currencies)
            setLanguages(data[0].languages)
            setLoading(false)
            
        })
        

    }, [])

    if(loading){
        return <div>loading</div>
    }

    const renderLang = () =>{
        for(let lang in languages){
            return languages[lang]
        }
    }
    return (    
        <Wrapper>
            <h2>{Object.values(nativeName)[0].common}</h2>
            <h2>{Object.values(currencies)[0].name}</h2>
            <h2>{languages.deu}</h2>
            <Back>Back</Back>
            <Container>
                <Image src = {country.flags.svg}/>
                <InfoContainer>
                    <Title>{country.name.common}</Title>
                    <InfoColumns>
                        <Column>
                            <Info><Strong>Native Name:</Strong>{country.name.nativeName.common}</Info>
                            <Info><Strong>Population:</Strong>{country.population}</Info>
                            <Info><Strong>Region:</Strong>{country.region}</Info>
                            <Info><Strong>Sub Region:</Strong>{country.subregion}</Info>
                            <Info><Strong>Capital:</Strong>{country.capital}</Info>
                        </Column>
                            <Info><Strong>Top Level Domain</Strong>{country.tld}</Info>
                            <Info><Strong>Currencies</Strong></Info>
                            <Info><Strong>Languages</Strong>{languages.map(renderLang)}</Info>
                        <Column>
                        </Column>
                    </InfoColumns>
                    <BordersContainer>
                        <BorderLabel>Border Countries</BorderLabel>
                        <Button>

                        </Button>
                    </BordersContainer>
                </InfoContainer>
            </Container>
        </Wrapper>
    )
}

export default DetailPage
